mapper.select:
    select 

    from RANDOM_EXAM_EMPLOYEE_ERROR


	       AND ITEM_ID = #{itemId,jdbcType=INTEGER}


	       AND EMPLOYEE_ID = #{employeeId,jdbcType=INTEGER}


	       AND TYPE = #{type,jdbcType=INTEGER}


	       AND EXAM_ID = #{examId,jdbcType=INTEGER}

SELECT t1.ERROR_ID, t1.ANSWER, t2.*, t3.BOOK_NAME, t4.CHAPTER_NAME, t5.CATEGORY_NAME, t6.SHORT_NAME, t7.TYPE_NAME, t8.DIFFICULTY_NAME, t9.STATUS_NAME FROM RANDOM_EXAM_EMPLOYEE_ERROR t1 LEFT JOIN ITEM t2 ON t2.ITEM_ID = t1.ITEM_ID LEFT JOIN BOOK t3 ON t3.BOOK_ID = t2.BOOK_ID LEFT JOIN BOOK_CHAPTER t4 ON t4.CHAPTER_ID = t2.CHAPTER_ID AND t4.BOOK_ID = t3.BOOK_ID LEFT JOIN ITEM_CATEGORY t5 ON t5.ITEM_CATEGORY_ID = t2.CATEGORY_ID LEFT JOIN ORG t6 ON t6.ORG_ID = t2.ORG_ID LEFT JOIN ITEM_TYPE t7 ON t7.ITEM_TYPE_ID = t2.TYPE_ID AND t7.IS_VALID = 1 LEFT JOIN ITEM_DIFFICULTY t8 ON t8.ITEM_DIFFICULTY_ID = t2.DIFFICULTY_ID LEFT JOIN ITEM_STATUS t9 ON t9.ITEM_STATUS_ID = t2.STATUS_ID
AND t3.BOOK_ID=#{bookId,jdbcType=INTEGER}
AND t1.EXAM_ID=#{bookId,jdbcType=INTEGER}
AND t2.TYPE_ID=#{typeId,jdbcType=INTEGER} AND t2.Status_ID=1 AND t2.Usage_Id=0 AND t1.TYPE = #{type,jdbcType=INTEGER} AND t1.EMPLOYEE_ID = #{employeeId,jdbcType=INTEGER}
order by t2.Item_id

mapper.delete:
delete from RANDOM_EXAM_EMPLOYEE_ERROR
where ITEM_ID = #{itemId,jdbcType=INTEGER} AND EMPLOYEE_ID = #{employeeId,jdbcType=INTEGER} 
    AND TYPE = #{type,jdbcType=INTEGER}
